import React, { useState } from "react";
import EmpCard from "./EmpCard";
import {useDispatch} from "react-redux";
import Form from "./Form";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import {useSelector} from "react-redux";


const Acm = () => {
  const dispatch = useDispatch();
  const AddEmployyeeEvent = () =>{
    dispatch({
            type: "ADD_EMPLOYEE", payload : {id:4,name:"Lehri abhishek",role:"manager",image:"./photo/ceo.png",email:"abhishek@gmail.com",number:"8788852578",salary:70000,time:"9:30"}}
            )
    }
  const {employee} = useSelector(state => state.EmployeeReducer)
  console.log(employee);
  const [form, setForm] = useState(false);
  const [empForm, setEmpForm] = useState({
    name: "",
    password: "",
    email: "",
    number: "",
    salary: "",
    time: "",
  });
  const [empData, setEmpData] = useState([]);
  const formEvent = () => {
    {
      form ? setForm(false) : setForm(true);
    }
  };
  const EmpEvent = (event) => {
    const { name, value } = event.target;
    setEmpForm((olddata) => {
      return { ...olddata, [name]: value };
    });
   
  };
  const addEmpData = () => {
    if (
      empForm.name === "" ||
      empForm.password === "" ||
      empForm.email === "" ||
      empForm.number === "" ||
      empForm.salary === "" ||
      empForm.time === ""
    ) {
      toast.warn("Invalid Information", {
        position: "top-right",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
      });
    } else {
      setEmpData((olddata) => {
        return [...olddata, empForm];
      });
      setEmpForm({
        name: "",
        password: "",
        email: "",
        number: "",
        salary: "",
        time: "",
      });
    }
    console.log(empForm.name);
  };
  return (
    <>
      <div className="container-fluid align-items-center">
        <div className="row d-flex">
          <div className="col-10 mx-auto">
            <div className="row d-flex flex-row justify-content-center align-items-center">
              <div className="col-lg-6 col-4">
                <h2 className="text-capitalize title text-left pt-3">
                  employee data
                </h2>
              </div>
              <div className="col-lg-4 col-4 abc">
                <input className="form-control" placeholder="Search" />
              </div>
              <div className="col-lg-2 col-4">
                <button
                  onClick={formEvent}
                  className="btn btn-outline-secondary text-uppercase"
                >
                  add new
                </button>
              </div>
              <hr />
            </div>
            <Form
              formvalue={form}
              data={empForm}
              onsave={AddEmployyeeEvent}
              onselect={EmpEvent}
            />
            <EmpCard emp={employee} />
          </div>
        </div>
      </div>
      <ToastContainer />
    </>
  );
};
export default Acm;
