import React from "react";
import { BsArrowRightShort } from "react-icons/bs";
import { useDispatch } from "react-redux";
import { NavLink } from "react-router-dom";
import EmployeeDetail from "./EmplyeeDetail";

const Table = (prop) => {
  const dispatch = useDispatch();
  const DeatailEvent = () => {
    dispatch({ type: "detail" });
  };
  return (
    <>
      <div className="c_table">
        <table className="table table-hover text-center table-bordered ">
          <thead className="fs-5 text-uppercase">
            <tr>
              <th scope="col">#</th>
              <th scope="col">Name</th>
              <th scope="col">Role</th>
              <th scope="col">Action</th>
            </tr>
          </thead>
          <tbody>
            {console.log(prop.content)}
            {prop.content.map((value, key) => {
              return (
                <>
                  <tr>
                    <th scope="row">{value.id}</th>
                    <td className="fs-6 text-uppercase">{value.name}</td>
                    <td className="text-uppercase"> {value.role}</td>
                    <td>
                      <NavLink to="/EmplyeeDetail">
                        <BsArrowRightShort
                          onClick={DeatailEvent}
                          className="fs-3 enter"
                        />
                      </NavLink>
                    </td>
                  </tr>
                </>
              );
            })}
          </tbody>
        </table>
      </div>
    </>
  );
};

export default Table;
